/** Replace the # icon in the Rich Text Controls to a link icon */
/** Call this function whenever a new instance of editor */

function restyleNewEditorIcons() {
    $('button[data-action="createLink"] b').html("<i class='fas fa-link'></i>");
    $('button[data-action="image"] b').html("<i class='fa-regular fa-image'></i>");
    $('button[data-action="html"] b').html("<i class='fa-solid fa-code'></i>")
}