/** Replace the # icon in the Rich Text Controls to a link icon */
/** Call this function whenever a new instance of editor */

function restyleNewEditorIcons() {
    $('button[data-action="createLink"] b').html("<i class='fas fa-link'></i>");
    $('button[data-action="image"] b').html("<i class='fa-regular fa-image'></i>");
    $('button[data-action="html"] b').html("<i class='fa-solid fa-code'></i>")
    $('button[data-action="justifyLeft"] b').html("<i class='fa-solid fa-align-left'></i>")
    $('button[data-action="justifyCenter"] b').html("<i class='fa-solid fa-align-center'></i>")
    $('button[data-action="justifyRight"] b').html("<i class='fa-solid fa-align-right'></i>")
    $('button[data-action="indent"] b').html("<i class='fa-solid fa-indent'></i>")
    $('button[data-action="outdent"] b').html("<i class='fa-solid fa-outdent'></i>")
    $('button[data-action="insertorderedlist"] b').html("<i class='fa-solid fa-list-ol'></i>")
    $('button[data-action="insertunorderedlist"] b').html("<i class='fa-solid fa-list-ul'></i>")

    // Break to next line because too many buttons
    $('.medium-editor-toolbar-actions li:nth-child(12)').css("clear", "both");


}