/** Replace the # icon in the Rich Text Controls to a link icon */

let untilReady = setInterval(()=>{

    const isReady = $('button[data-action="createLink"] b').length;
    if(isReady) {
        $('button[data-action="createLink"] b').html("<i class='fas fa-link'></i>");
        clearInterval(untilReady);
    }
}, 200);